INSERT INTO `c_section` VALUES (1,'৫.১ কম্পিউটার প্রোগ্রাম'),(2,'৫.২ প্রোগ্রামিং ভাষা'),(3,'৫.৩ অনুবাদক প্রোগ্রাম'),(4,'৫.৪ প্রোগ্রাম তৈরির ধাপসমূহ'),(5,'৫.৫ বিভিন্ন রকমের প্রোগ্রামিং কাঠামো'),(6,'৫.৬ প্রোগ্রাম ডিবাগিং'),(7,'৫.৭ সি প্রোগ্রামের মৌলিক ধারণা'),(8,'ব্যবহারিক'),(9,'সার-সংক্ষেপ'),(10,'অনুশীলনী'),(11,'প্রাথমিক ধারণা'),(12,'১.১ বিশ্বগ্রামের ধারণা'),(13,'১.২ ভার্চুয়াল রিয়েলিটি'),(14,'১.৩ তথ্য ও যোগাযোগ প্রযুক্তি'),(15,'১.৪ তথ্য ও যোগাযোগ প্রযুক্তির সামপ্রতিক প্রবণতা'),(16,'১.৫ তথ্য ও যোগাযোগ প্রযুক্তি ব্যবহারে নৈতিকতা'),(17,'১.৬ সমাজ জীবনে তথ্য ও যোগাযোগ প্রযুক্তির প্রভাব'),(18,'১.৭ তথ্য ও যোগাযোগ প্রযুক্তি এবং অর্থনৈতিক উন্নয়ন'),(19,'১.৮ কতিপয় গাণিতিক সমস্যাবলি'),(20,'শিখন ফল'),(21,'২.১ কমিউনিকেশন সিস্টেম'),(22,'২.২ ডেটা কমিউনিকেশন মাধ্যমে'),(23,'২.৩ ওয়ারলেস কমিউনিকেশন সিস্টেম'),(24,'২.৪ মোবাইল যোগাযোগ'),(25,'২.৫ কম্পিউটার নেটওয়ার্কিং'),(26,'২.৬ কতিপয় গাণিতিক ও বিশেষধর্মী সমস্যা');
