INSERT INTO `c_solution` VALUES (1,1,'C','#include<stdio.h>\n int main(){\n printf(\"Name: Sakib Hasan \\nAddress: House-102, Road no 5, Sector  11, Uttara, Dhaka.\");\n return 0;\n}','admin','2016-09-20 12:48:40',NULL),(2,2,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    char char_variable=\'a\';\r\n    char char_asci_variable=65;\r\n    int decimal_variable=2016;\r\n    long int log_decimal_variable=20161216;\r\n    float float_variable=3.1416;\r\n    printf (\"Characters: %c %c \\n\", char_variable, char_asci_variable);\r\n    printf (\"%s \\n\", \"A string\");\r\n    printf (\"Decimals: %d %ld\\n\", decimal_variable, log_decimal_variable);\r\n    printf (\"Preceding with blanks: %8d \\n\", decimal_variable);\r\n    printf (\"Preceding with zeros : %08d \\n\", decimal_variable);\r\n    printf (\"Floats: %f %4.2f %+.0e %E \\n\", float_variable, float_variable, float_variable, float_variable);\r\n    printf (\"Hexadecimal of %d: %x \\n\", decimal_variable, decimal_variable);\r\n    printf (\"Octal of %d: %o \\n\", decimal_variable, decimal_variable);\r\n    return 0;\r\n}','admin','2016-09-21 11:45:39','2016-11-21 01:49:11'),(3,3,'C','#include <stdio.h>\nint main()\n{\n   int age;\n   printf(\"Enter your age:\");\n   scanf(\"%d\",&age);\n   if(age<14){\n       printf(\"Sorry Kids! This may not safe for you.\");\n   }\n    else{\n        printf(\"Welcome to thriller ride!\");\n    }\n   return 0;\n}\n','admin','2016-09-24 11:52:02',NULL),(4,4,'C','#include <stdio.h>\n#include <stdlib.h>\nint main ()\n{\n    int n, f;\n	printf(\"Please enter your Building No. and Floor No. ....\");\n	scanf(\"%d\", &n);\n	scanf(\"%d\", &f);\n	if (n == 1){\n		printf(\"Go to the East\");\n	if (f == 1) printf(\"You are going to Civil Engg. deptt.\");\n		else if (f == 2) printf(\"You are going to Mechanical Engg. Deptt.\");\n		else printf(\"You are going to Economics Deptt.\");\n	}\n	else if (n == 2){\n	printf(\"Go to the West\");\n		if (f == 1) printf(\"You are going to Computer Engg. Deptt.\");\n		else if (f == 2) printf(\"You are going to Electrical Engg. Deptt.\");\n		else printf(\"You are going to Chemical Engg. Deptt.\");\n	}\n	else if (n == 3){\n	printf(\"Go to the North\");\n		if (f == 1) printf(\"You are going to English Deptt.\");\n		else printf(\"You are going to Physics Deptt.\");\n	}\n	else{\n	printf(\"Go to the South\");\n		if (f == 1) printf(\"You are going to Chemistry Deptt.\");\n		else printf(\"You are going to Mathematics Deptt.\");\n	}\n	return 0;\n}','admin','2016-09-26 07:56:28',NULL),(5,5,'C','#include<stdio.h>\nmain()\n{\n    float radius, area;\n    printf(\"Radius? =\");\n    scanf(\"%f\", &radius);\n    area=3.14159*radius*radius;\n    printf(\"Area=%f\", area);\n}','admin','2016-10-03 10:42:26',NULL),(6,6,'C','#include <stdio.h>\nint main() {\n    int first_paper_mark, second_paper_mark, total;\n    first_paper_mark=75;\n    second_paper_mark=85;\n    total=first_paper_mark+second_paper_mark;\n    printf(\"Congratulations! Sakib you got total 160 marks of English two parts.\",total);\n    return 0;\n}','admin','2016-10-03 11:34:12',NULL),(7,7,'C','#include <stdio.h>\nint main(void)\n{\n  int x, y, choice;\n    printf(\"Please Enter First Integer\\n\");\n    scanf(\"%d\", &x);\n    printf(\"Please Enter Second Integer\\n\");\n    scanf(\"%d\", &y);\n    printf(\"What do you want to do? Enter 1 (add), 2 (subtract), \n3 (Multiply) or 4 (Division)\\n\");\n    scanf(\"%d\", &choice);\n    switch(choice)\n    {\n	case 1:\n		printf(\"%d + %d = %d\\n\", x, y, x+y);\n		break;\n	case 2:\n	   	printf(\"%d - %d = %d\\n\", x, y, x-y);\n		break;\n	case 3:\n	   	printf(\"%d * %d = %d\\n\", x, y, x*y);\n		break;\n   	case 4:\n		printf(\"%d / %d = %f\\n\", x, y, (float)x/y);\n	   	break;\n   	default:\n		printf(\"Operation Not Supported!\\n\");\n }\n  return 0;\n}','admin','2016-10-05 13:18:14',NULL),(8,8,'C','#include <stdio.h>\nchar *subjects[]={\"Bangla\", \"English\", \"Physics\", \"Chemistry\", \n\"Mathematics\", \"ICT\", \"Biology\", NULL};\nint *marks[7]={76, 81, 92, 89, 93, 73, 83, NULL}, i, sum = 0, count=0;\nfloat average;\nint main () {\n	char **i = subjects;\n	int **j = marks;\n	while (*i) {\n		printf(\"%s=%d \\n\", *i, *j);\n		sum += (int)*j;\n		i++;\n		j++;\n		count++;\n	}\n	average = (float)(sum/count);\n	printf(\"\\nTotal marks = %d\\n\", sum);\n	printf(\"Average marks = %.2f\", average);\n}','admin','2016-11-03 05:56:12',NULL),(9,9,'C','#include<stdio.h>\r\nint main()\r\n{\r\n    int i,j;\r\n    printf(\"Multiplication table from 1 to 10 \\n\");\r\n    for(i=1;i<=10;i++)\r\n    {\r\n        for(j=1;j<=10;j++)\r\n        printf(\"%4d\",i*j);\r\n        printf(\"\\n\");\r\n    }\r\n    return 0;\r\n}','admin','2016-11-03 06:37:21','2016-11-21 03:00:21'),(10,10,'C','#include <stdio.h>\r\n void main()\r\n {\r\n     int result;\r\n     printf(\"Enter your total mark:\");\r\n     scanf(\"%d\",&result);\r\n     if(result<450) {\r\n        printf(\"You are selected for section:B\\n\");\r\n     }\r\n    else{\r\n        printf(\"You are selected for section:A\\n\");\r\n    }\r\n }','admin','2016-11-20 13:24:59',NULL),(11,11,'C','#include <stdio.h>\r\nint main()\r\n     {\r\n     int numbers[]={2,0,1,6};\r\n     int i;\r\n     printf(\"Array elements are\\n\");\r\n     for(i=0;i<=3;i++)\r\n              printf(\"%d\\n\",numbers[i]);\r\n     return 0;\r\n    }','admin','2016-11-23 14:12:20',NULL),(12,12,'C','#include <stdio.h>\r\nint power(int a, int b)\r\n{\r\n    int i, result;\r\n    result = 1;\r\n    for(i =0;i< b ; i++)\r\n    {\r\n        result = result * a;\r\n    }\r\n    return result;\r\n}\r\nint main(void)\r\n{\r\n    int x, y, powerResult;\r\n    printf(\"Please Enter two integers\\n To quit enter 0 for the second integer\\n\");\r\n    scanf(\"%d\", &x);\r\n    scanf(\"%d\", &y);\r\n    while(y!=0)\r\n    {\r\n        powerResult = power(x,y);\r\n        printf(\"%d + %d = %d\\n\", x, y, x+y);\r\n        printf(\"%d - %d = %d\\n\", x, y, x-y);\r\n        printf(\"%d * %d = %d\\n\", x, y, x*y);\r\n        printf(\"%d / %d = %f\\n\", x, y, (float)x/y);\r\n        printf(\"%d ^ %d = %d\\n\", x, y, powerResult);\r\n        scanf(\"%d\", &x);\r\n        scanf(\"%d\", &y);\r\n    }\r\n    return 0;\r\n}','admin','2016-11-23 14:30:21',NULL),(13,13,'C','#include <stdio.h>\r\nint fact(int n);\r\nint main(void)\r\n{\r\n	int number, result;\r\n	printf(\"Enter a positive integer:\");\r\n	scanf(\"%d\", &number);\r\n	result = 1;\r\n    result = fact(number);\r\n    printf(\"The factorial of %d is %d\\n\", number, result);\r\n}\r\nint fact(int n)\r\n{\r\n	if(n==1) return n;\r\n	return n*fact(n-1);\r\n}','admin','2016-11-26 11:08:32',NULL),(14,14,'C','#include <stdio.h>\r\nint main()\r\n{\r\n   int first, second, *a, *b, add ;\r\n   printf(\"Enter two values:\\n\");\r\n   scanf(\"%d%d\", &first, &second);\r\n   a = &first;\r\n   b = &second;\r\n   add = *a + *b;\r\n   printf(\"Sum of %d and %d is: %d\",first, second, add);\r\n   return 0;\r\n}','admin','2016-11-26 13:11:14',NULL),(15,15,'C','#include <stdio.h>\r\nint main(){\r\n    int num1, num2, sum;\r\n    printf(\"Enter the two integer values to be added: \");\r\n    scanf(\"%d %d\", &num1, &num2);\r\n    sum=num1+num2;\r\n    printf(\"Addition of %d and %d = %d\\n\", num1, num2, sum);\r\n    return 0;\r\n}','admin','2017-04-25 15:47:48',NULL),(16,16,'C','#include <stdio.h>\r\n#include <ctype.h>\r\nint main()\r\n{\r\n    char ch;\r\n    printf(\"Enter any character value: \");\r\n    ch = getchar();\r\n    printf(\"The equivalent uppercase character is: \");\r\n    putchar(toupper(ch));\r\n    return 0;\r\n}','admin','2017-05-02 16:04:15',NULL),(17,17,'C','#include <stdio.h>\r\nint main() {\r\n    int i=12345;\r\n    float x=345.678;\r\n    printf (\"%3d %5d %8d\\n\\n\", i, i, i);\r\n    printf (\"%3f %10f %13f\\n\\n\", x, x, x);\r\n    printf (\"%3e %13e %16e\", x, x, x);\r\n    return 0;\r\n}','admin','2017-05-02 20:50:40',NULL),(18,18,'C','#include <stdio.h>\r\n#include <math.h>\r\nmain( ){\r\nfloat p, r, n, i, f;\r\nprintf(\"P1ease enter a value for the principal (P): \");\r\nscanf ( \"%f\", &p);\r\nprintf(\"P1ease enter a value for the interest rate (r): \") ;\r\nscanf ( \"%f\", &r );\r\nprintf(\"P1ease enter a value for the number of years (n): \");\r\nscanf ( \"%f\", &n);\r\ni= r/100;\r\nf = p*pow((1 + i ), n);\r\nprintf(\"\\nThe final value (F) is: %.2f\\n \", f ) ;\r\n}','admin','2017-05-04 12:04:24',NULL),(19,19,'C','#include <stdio.h>\r\nlong int factorial (int n);\r\nmain( )\r\n{\r\n    auto int n;\r\n    printf(\"\\nn = \");\r\n    scanf(\"%d\", &n);\r\n    printf(\"\\nn! = %ld\", factorial(n));\r\n}\r\nlong int factorial(int n)\r\n{\r\n    auto int i;\r\n    auto long int prod=1;\r\n    if(n>1)\r\n        for(i=2; i<=n; ++i)\r\n            prod*=i;\r\n    return (prod);\r\n}','admin','2017-05-06 20:19:18',NULL),(20,20,'C','#include <stdio.h>\r\nint main(void)\r\n{\r\n    printf(\"%d \", 5/2);\r\n    printf(\"%d \", 5%2);\r\n    printf(\"%d \", 4/2);\r\n    printf(\"%d \", 4%2);\r\n    return 0;\r\n}','admin','2017-05-08 20:02:16',NULL),(21,21,'C','#include <stdio.h>\r\nint main(void)\r\n{\r\n    float num;\r\n    int choice;\r\n    printf(\"1: Feet to Meters, 2: Meters to Feet. \");\r\n    printf(\"Enter choice: \");\r\n    scanf(\"%d\", &choice);\r\n    if(choice==1){\r\n        printf(\"Enter number of feet: \");\r\n        scanf(\"%f\", &num);\r\n        printf(\"Meters %f\", num/3.28);\r\n    }\r\n    else {\r\n        printf(\"Enter number of meters: \");\r\n        scanf(\"%f\", &num);\r\n        printf(\"Meters %f\", num*3.28);\r\n    }\r\n    return 0;\r\n}\r\n','admin','2017-05-09 19:07:26',NULL),(22,22,'C','#include <stdio.h>\r\nlong int recursion(int n);\r\nint main()\r\n{\r\n    int n;\r\n    printf(\"Enter a positive integer: \");\r\n    scanf(\"%d\", &n);\r\n    printf(\"Factorial of %d = %ld\", n, recursion(n));\r\n    return 0;\r\n}\r\nlong int recursion(int n)\r\n{\r\n    if (n >= 1)\r\n        return n*recursion(n-1);\r\n    else\r\n        return 1;\r\n}','admin','2017-05-10 13:03:33',NULL),(23,23,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    long num, temp, digit, sum = 0;\r\n    printf(\"Enter number: \");\r\n    scanf(\"%ld\", &num);\r\n    temp = num;\r\n    while (num > 0)\r\n    {\r\n        digit = num % 10;\r\n        sum  = sum + digit;\r\n        num /= 10;\r\n    }\r\n    printf(\"Given number = %ld\\n\", temp);\r\n    printf(\"Sum of the digits %ld = %ld\\n\", temp, sum);\r\n    return 0;\r\n}','admin','2017-05-10 17:27:59',NULL),(24,24,'C','# include <stdio.h>\r\nint main()\r\n{\r\n    int i, n ;\r\n    printf(\"Enter the number : \") ;\r\n    scanf(\"%d\", &n) ;\r\n    printf(\"\\nThe divisors are :\") ;\r\n    for(i = 1 ; i <= n ; i++)\r\n        if(n % i == 0)\r\n        printf(\"%d \", i);\r\n    return 0;\r\n}','admin','2017-05-10 19:20:45',NULL),(25,25,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    int n, i, flag = 0;\r\n    printf(\"Enter a positive integer: \");\r\n    scanf(\"%d\",&n);\r\n    for(i=2; i<=n/2; ++i)\r\n    {\r\n        if(n%i==0)\r\n        {\r\n            flag=1;\r\n            break;\r\n        }\r\n    }\r\n    if (flag==0)\r\n        printf(\"%d is a prime number.\",n);\r\n    else\r\n        printf(\"%d is not a prime number.\",n);\r\n    return 0;\r\n}','admin','2017-05-10 20:07:11',NULL),(26,26,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    int n, reversedNumber = 0, remainder;\r\n    printf(\"Enter an integer: \");\r\n    scanf(\"%d\", &n);\r\n    while(n != 0)\r\n    {\r\n        remainder = n%10;\r\n        reversedNumber = reversedNumber*10 + remainder;\r\n        n /= 10;\r\n    }\r\n    printf(\"Reversed Number = %d\", reversedNumber);\r\n    return 0;\r\n}','admin','2017-05-11 13:10:16',NULL),(27,27,'C','#include<stdio.h>\r\nint main() {\r\n   int num, i = 1, sum = 0, j=1;\r\n   printf(\"Enter a number: \");\r\n   scanf(\"%d\", &num);\r\n   while (i < num) {\r\n      if (num % i == 0) {\r\n         sum = sum + i;\r\n      }\r\n      i++;\r\n   }\r\n   if (sum == num){\r\n        printf(\"%d = \", num);\r\n           while (j < num) {\r\n              if (num % j == 0) {\r\n                 sum = sum + j;\r\n                 if(j==1){\r\n                    printf(\"1\");\r\n                 }\r\n                 else\r\n                 printf(\"+%d\", j);\r\n              }\r\n              j++;\r\n           }\r\n   }\r\n   else\r\n      printf(\"Not Perfect\", i);\r\n\r\n   return 0;\r\n}','admin','2017-05-11 16:39:43',NULL),(28,28,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    int n;\r\n    printf(\"Input: \");\r\n    scanf(\"%d\", &n);\r\n    int f1 = 0, f2 = 1, i;\r\n    printf(\"Output: \");\r\n    if (n < 1)\r\n        return;\r\n    for (i = 1; i <= n; i++)\r\n    {\r\n        printf(\"%d \", f2);\r\n        int next = f1 + f2;\r\n        f1 = f2;\r\n        f2 = next;\r\n    }\r\n    return 0;\r\n}','admin','2017-05-11 17:49:12',NULL),(29,29,'C','#include <stdio.h>\r\nint getFactorial(int N);\r\nint main(){\r\n    int n, r, nPr;\r\n    printf(\"Enter n and r for nPr calculation: \");\r\n    scanf(\"%d %d\",&n, &r);\r\n\r\n    nPr = getNPR(n, r);\r\n    if(nPr == -1){\r\n        printf(\"Invalid Input: n must be >= r\\n\");\r\n    } else {\r\n        printf(\"%dP%d = %d\\n\", n, r, nPr);\r\n    }\r\n    return 0;\r\n}\r\nint getNPR(int n, int r){\r\n     if(r > n){\r\n         return -1;\r\n     }\r\n     return getFactorial(n)/getFactorial(n - r);\r\n }\r\n int getFactorial(int N){\r\n    if(N < 0){\r\n        printf(\"Invalid Input\");\r\n        return 0;\r\n    }\r\n    int nFactorial, counter;\r\n    /*  N! = N*(N-1)*(N-2)*(N-3)*.....*3*2*1  */\r\n    for(counter = 1, nFactorial = 1; counter <= N; counter++){\r\n        nFactorial = nFactorial * counter;\r\n    }\r\n    return nFactorial;\r\n}','admin','2017-05-11 18:42:40',NULL),(30,30,'C','#include <stdio.h>\r\nint main(){\r\n    int n, i, j;\r\n    printf(\"Enter n: \");\r\n    scanf(\"%d\",&n);\r\n    for(i=1; i<=n; i++){\r\n        for(j=1; j<=i; j++){\r\n            printf(\"*\");\r\n        }\r\n        printf(\"\\n\");\r\n    }\r\n    return 0;\r\n}','admin','2017-05-13 18:15:20',NULL),(31,31,'C','#include <stdio.h>\r\nint main(){\r\n    int n, i, j, k;\r\n    printf(\"Enter n: \");\r\n    scanf(\"%d\",&n);\r\n    for(i=1; i<=n; i++){\r\n        for(j=1; j<=n-i; j++){\r\n            printf(\" \");\r\n        }\r\n        for(k=1;k<=i; k++){\r\n            printf(\"* \");\r\n        }\r\n        printf(\"\\n\");\r\n    }\r\n    return 0;\r\n}','admin','2017-05-13 18:35:33',NULL),(32,32,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    int n1, n2, i, gcd;\r\n    printf(\"Input: \");\r\n    scanf(\"%d %d\", &n1, &n2);\r\n    for(i=1; i <= n1 && i <= n2; ++i)\r\n    {\r\n        if(n1%i==0 && n2%i==0)\r\n            gcd = i;\r\n    }\r\n    printf(\"Output: %d\", gcd);\r\n    return 0;\r\n}\r\n','admin','2017-05-13 19:12:05',NULL),(33,33,'C','#include <stdio.h>\r\nint main()\r\n{\r\n    int i, space, rows, k=0, count = 0, count1 = 0;\r\n    printf(\"Input: \");\r\n    scanf(\"%d\",&rows);\r\n    for(i=1; i<=rows; ++i)\r\n    {\r\n        for(space=1; space <= rows-i; ++space)\r\n        {\r\n            printf(\" \");\r\n            ++count;\r\n        }\r\n        while(k != 2*i-1)\r\n        {\r\n            if (count <= rows-1)\r\n            {\r\n                printf(\"%d\", i+k);\r\n                ++count;\r\n            }\r\n            else\r\n            {\r\n                ++count1;\r\n                printf(\"%d\", (i+k-2*count1));\r\n            }\r\n            ++k;\r\n        }\r\n        count1 = count = k = 0;\r\n        printf(\"\\n\");\r\n    }\r\n    return 0;\r\n}','admin','2017-05-14 18:54:21',NULL),(34,34,'C','#include <stdio.h>\r\nint main(){\r\n    char str[10];\r\n    int i;\r\n    printf(\"Enter a string less than 10 char: \");\r\n    gets(str);\r\n    for(i=0; str[i]; i++){\r\n        printf(\"%c\", toupper(str[i]));\r\n    }\r\n    return 0;\r\n}','admin','2017-05-30 04:38:26',NULL),(35,35,'C','#include <stdio.h>\r\n\r\nchar *mystrstr(char *str1, char* str2)\r\nint main()\r\n{\r\n   char *str1;\r\n   char *str2;\r\n   str1 = (char *) malloc (100 * sizeof(char));\r\n   str2 = (char *) malloc (100 * sizeof(char));\r\n\r\n   gets(str1);\r\n   gets(str2);\r\n\r\n   printf(\"%s\\n\", mystrstr(str1, str2));\r\n   free(str1);\r\n   free(str2);\r\n\r\n   return 0;\r\n}\r\nchar *mystrstr(char *str1, char* str2) {\r\n\r\n    char* first_occ = NULL;\r\n\r\n    while(*str1) {\r\n        if(*str1++ == *str2) {\r\n            if(first_occ == NULL){\r\n                first_occ = str1;\r\n            }\r\n            char const *a = str2;\r\n            while((*str1++ == *++a) && (*a));  /*empty*/\r\n            if(*a == \'\\0\')\r\n                return first_occ;\r\n        }\r\n    }\r\n    return first_occ;\r\n}','admin','2017-06-04 07:53:00',NULL),(36,36,'C','#include <stdio.h>\r\nint main(){\r\n   int marks;\r\n   printf(\"Enter your marks between 0 to 100: \");\r\n   scanf(\"%d\", &marks);\r\n   switch(marks/5){\r\n        case 20 :\r\n        case 19 :\r\n        case 18 :\r\n        case 17 :\r\n        case 16 :\r\n           printf(\"Your Grade : A+\\n\" );\r\n           break;\r\n        case 15 :\r\n           printf(\"Your Grade : A\\n\" );\r\n           break;\r\n        case 14 :\r\n           printf(\"Your Grade : A-\\n\" );\r\n           break;\r\n       case 13 :\r\n           printf(\"Your Grade : B+\\n\" );\r\n           break;\r\n        case 12 :\r\n           printf(\"Your Grade : B\\n\" );\r\n           break;\r\n        default :\r\n           printf(\"Your Grade : D\" );\r\n   }\r\n   return 0;\r\n}','admin','2017-06-05 10:09:01',NULL),(37,37,'C','#include <stdio.h>\r\nint main(){\r\n    int h, m, p, q;\r\n    printf(\"Enter h m p q: \");\r\n    scanf(\"%d %d %d %d\", &h, &m, &p, &q);\r\n    h=h+p;\r\n    m=m+q;\r\n    if (m>59){\r\n        m=m-60;\r\n        h=h+1;\r\n    }\r\n    if(h>12){\r\n            h=h%12;\r\n        }\r\n    printf(\"%02d:\", h);\r\n    printf(\"%02d\", m);\r\n    return 0;\r\n}\r\n','admin','2017-06-05 10:43:05',NULL),(38,38,'C','#include <stdio.h>\r\n#include <math.h>\r\nlong long convertDecimalToBinary(int n);\r\n\r\nint main()\r\n{\r\n    int n;\r\n    printf(\"Enter a decimal number: \");\r\n    scanf(\"%d\", &n);\r\n    printf(\"%d decimal = %lld\", n, convertDecimalToBinary(n));\r\n    return 0;\r\n}\r\nlong long convertDecimalToBinary(int n)\r\n{\r\n    long long binaryNumber = 0;\r\n    int remainder, i = 1, step = 1;\r\n\r\n    while (n!=0)\r\n    {\r\n        remainder = n%2;\r\n        n /= 2;\r\n        binaryNumber += remainder*i;\r\n        i *= 10;\r\n    }\r\n    return binaryNumber;\r\n}\r\n','admin','2017-06-05 11:22:58',NULL);
