INSERT INTO `c_test_problem` VALUES (1,'2',1,'মেসেজ প্রর্দশন','সাকিব SSC সদ্য পাশ করে এখন সে একটি কলেজে ভর্তি হয়েছে। সবই ঠিক আছে কিন্তু একটি বিষয় তার কাছে নতুন তা হল তথ্য ও প্রযুক্তি বিষয়টি। এখানে আছে প্রোগ্রামিং ভাষা নামে একটি chapter। যেখানে আছে কম্পিউটার ভাষা নিয়ে আলোচনা। প্রোগ্রামিং ভাষা আয়ত্তে আনতে সে একটি প্রোগ্রাম লিখতে চায় যেখানে তার নাম ও ঠিকানা লিখা দেখাবে।\r\nএই জন্য একটি প্রোগ্রাম লিখ যেখানে তার নাম ও ঠিকানা লিখা দেখায়।','এই প্রোগ্রামটি লিখতে পেরে সাকিব যা শিখল তা হল। প্রোগ্রামে কিভাবে মেসেজ দেখাতে হয়। কিভাবে নতুন লাইন দিতে হয় ইত্যাদি। ','Lesson 5, Section 5.4, Page 258','int','C',1,'','Name: Sakib Hasan\r\nAddress: House-102, Road no 5, Sector  11, Uttara, Dhaka.',10.00,'admin','2017-02-01 04:48:34',NULL,'00:30:00',NULL);
/*!40000 ALTER TABLE `c_test_problem` ENABLE KEYS */;
UNLOCK TABLES;

